collection.formula:
$knk = 3.999996;
$original_cost_stones = 0;
$selected_cost_stones = 0;
$original_weight_stones = 0;
$original_ring_cost_metal = 0;
$select_ring_cost_metal = 0;

if(isset($ring_info_m['ring_original']))
{
//------------------------------------------------------------
//$original_cost_stones - стоимость вставки
if($ring_info_m['ring_original']['stones'])
{
foreach($ring_info_m['ring_original']['stones'] as $stone)
{
$original_cost_stones += $stone['count'] * $stone['price'] * $stone['karatnost'];
$original_weight_stones += $stone['count'] * $stone['karatnost'] /5;

}
$original_cost_stones = $original_cost_stones * $knk;
}
else
{
	$original_cost_stones = 0;
	$original_weight_stones = 0;
}
//------------------------------------------------------------
//$selected_cost_stones - стоимость выбранной вставки
if($ring_info_m['selected_stones'])
{
 foreach($ring_info_m['selected_stones']['size'] as $stone)
{
  foreach($stone as $s)
{
  $selected_cost_stones += $s['karatnost'] * $s['price'] * $s['count'];
$stone_type = $s['color_ru'];

}
$selected_cost_stones = $selected_cost_stones * $knk;
}
}
else
{
    $selected_cost_stones = $original_cost_stones;
   
}

//стоимость оригинального метала
switch($ring_info_m['ring_original']['metal']['probe']) {
		case '585': $cost_metal = 30; $koef_metal = 1; $koef_oncost = 3.999996; break;
		case '950': $cost_metal = 65; $koef_metal = 1.52; $koef_oncost = 3.999996; break;
		case '850': $cost_metal = 23; $koef_metal = 0.83; $koef_oncost = 7; break; 
		case '750': $cost_metal = 40; $koef_metal = 1.2; $koef_oncost = 3.999996; break; 
	}
$original_ring_cost_metal = ($ring_info_m['ring_original']['weight'] - $original_weight_stones) * $cost_metal * $koef_metal * $koef_oncost;	
$original_ring_cost_metal = ceil($original_ring_cost_metal/10)*10*8.5;
//------------------------------------------------------------
//стоимость выбранного метала
if($ring_info_m['selected_metal'])
	{
	switch($ring_info_m['selected_metal']['probe']) {
		case '585': $cost_metal = 30; $koef_metal = 1; $koef_oncost = 3.999996; break;
		case '950': $cost_metal = 65; $koef_metal = 1.52; $koef_oncost = 3.999996; break;
		case '850': $cost_metal = 23; $koef_metal = 0.83; $koef_oncost = 7; break; // не забыть поделить наценку
		case '750': $cost_metal = 40; $koef_metal = 1.2; $koef_oncost = 3.999996; break;
		
	}	
$select_ring_cost_metal = ($ring_info_m['ring_original']['weight'] - $original_weight_stones)* $cost_metal * $koef_metal * $koef_oncost;
$select_ring_cost_metal = ceil($select_ring_cost_metal/10)*10*8.5;
	}
else
{
   $select_ring_cost_metal = $original_ring_cost_metal;
}
//------------------------------------------------------------
//ФОРМУЛА
if($ring_info_m['ring_original']['active_st_size'])
{
	if(!$ring_info_m['selected_stones'])
	{
		$new_price_m = ($ring_info_m['ring_original']['price'] - $original_ring_cost_metal + $select_ring_cost_metal)* $ring_info_m['selected_size']/$ring_info_m['ring_original']['size'];
	}
	else
	{
		if($stone_type === 'Белый')
		{
		$new_price_m = (($ring_info_m['ring_original']['price'] - $original_ring_cost_metal) + $select_ring_cost_metal)* $ring_info_m['selected_size']/$ring_info_m['ring_original']['size'];
		}
		else
		{
		$new_price_m = ($selected_cost_stones + $select_ring_cost_metal)* $ring_info_m['selected_size']/$ring_info_m['ring_original']['size'];
		}
	}
}
else
{
	if(!$ring_info_m['selected_stones'])
	{
		$new_price_m = $select_ring_cost_metal * $ring_info_m['selected_size']/$ring_info_m['ring_original']['size'] + ($ring_info_m['ring_original']['price'] - $original_ring_cost_metal);
	}
        else
        {
		if($stone_type === 'Белый')
		{
		$new_price_m = $select_ring_cost_metal * $ring_info_m['selected_size']/$ring_info_m['ring_original']['size'] + ($ring_info_m['ring_original']['price'] - $original_ring_cost_metal);

		}
		else
		{
		$new_price_m = $select_ring_cost_metal * $ring_info_m['selected_size']/$ring_info_m['ring_original']['size'] + $selected_cost_stones;

		}
	}
}



}




